<script lang="ts">
import { Component, Vue } from "vue-property-decorator";
import { EventBus } from '../lib/eventBus';
import { store } from '../store';
import { ScrollStatus } from '../constant/status';

function spotSpotlight(spot: HTMLElement, sy: number) {
  let scaleLavel = sy % 2000;
  if (scaleLavel === 0) scaleLavel = 1;
  if (scaleLavel < 1000) {
    const scale = 1 + scaleLavel / 1000 * 0.3;
    spot.style.transform = `scale(${scale}, ${scale})`
  } else {
    const scale = 1.6 - scaleLavel / 1000 * 0.3;
    spot.style.transform = `scale(${scale}, ${scale})`
  }
}

@Component
export default class FixedMap extends Vue {
  constructor() {
    super();
  }

  mounted() {
    const spotMap = new Map();
    spotMap.set(ScrollStatus.PART_1, document.getElementById('spot-1') as HTMLElement);
    spotMap.set(ScrollStatus.PART_2, document.getElementById('spot-2') as HTMLElement);

    this.$store.watch(
      function(state, getters) {
        return state.scrollStatus;
      }, function (value, oldValue) {
          spotMap.get(value).style.transform = `scale(1.3, 1.3)`;
          spotMap.forEach((spot, key) => {
            if (key !== value) spot.style.transform = `scale(1, 1)`;
          });
    });
  }

  onMapMouseOver(id: string): void {
    const spot = document.getElementById(id) as HTMLElement;
    spot.style.transform = "scale(1.3, 1.3)"
  }

  onMapMouseOut(id: string): void {
    const spot = document.getElementById(id) as HTMLElement;
    spot.style.transform = "scale(1, 1)"
  }
}
</script>

